import React, { useState, useEffect } from 'react';
import './styles.css';

const API_BASE = 'http://localhost:3001';

function App() {
  const [showLogin, setShowLogin] = useState(true);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  // Login form state
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Register form state
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const showMsg = (text, type) => {
    setMessage(text);
    setMessageType(type);
  };

  const clearMsg = () => {
    setMessage('');
    setMessageType('');
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    clearMsg();

    try {
      const response = await fetch(`${API_BASE}/user/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
      });

      const data = await response.json();

      if (!response.ok) {
        showMsg(data.message || 'Error al ingresar', 'error');
        return;
      }

      localStorage.setItem('currentUser', JSON.stringify(data.data));
      showMsg('Proceso de autenticaci√≥n finalizado. Redireccionamiento en curso...', 'success');
      setTimeout(() => window.location.href = 'mapa.html', 1000);
    } catch (error) {
      showMsg('No conecto al servidor', 'error');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    clearMsg();

    if (newPassword !== confirmPassword) {
      showMsg('Las contrase√±as no coinciden', 'error');
      return;
    }

    try {
      // Verificar si el usuario ya existe
      const response = await fetch(`${API_BASE}/user`);
      const users = await response.json();

      const userExists = users.find(u => u.username === newUsername);
      if (userExists) {
        showMsg('Credenciales ya exisntentes', 'error');
        return;
      }

      // Crear nuevo usuario
      const newUser = { username: newUsername, password: newPassword };
      const createResponse = await fetch(`${API_BASE}/user`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newUser)
      });

      if (createResponse.ok) {
        showMsg('La cuenta a ha sido creada correctamente, redirigiendo... ', 'success');
        setNewUsername('');
        setNewPassword('');
        setConfirmPassword('');
        setTimeout(() => setShowLogin(true), 1500);
      } else {
        showMsg('Creaci√≥n de usuario fallida', 'error');
      }
    } catch (error) {
      showMsg('Error al conectar con el servidor', 'error');
    }
  };

  // Verificar si ya hay un usuario logueado
  useEffect(() => {
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      window.location.href = 'mapa.html';
    }
  }, []);

  return (
    <div className="login-container">
      <h1>üó∫Ô∏è Turismo App</h1>
      
      <div className="form-section">
        {showLogin ? (
          <>
            <form onSubmit={handleLogin}>
              <div className="form-group">
                <label htmlFor="username">Usuario:</label>
                <input
                  type="text"
                  id="username"
                  placeholder="Ingresa tu usuario"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="password">Contrase√±a:</label>
                <input
                  type="password"
                  id="password"
                  placeholder="Ingresa tu contrase√±a"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary">Iniciar Sesi√≥n</button>
            </form>

            <div className="separator">o</div>

            <button onClick={() => { setShowLogin(false); clearMsg(); }} className="btn btn-secondary">
              Crear Nueva Cuenta
            </button>
          </>
        ) : (
          <>
            <form onSubmit={handleRegister}>
              <div className="form-group">
                <label htmlFor="newUsername">Nuevo Usuario:</label>
                <input
                  type="text"
                  id="newUsername"
                  placeholder="Elige un usuario"
                  value={newUsername}
                  onChange={(e) => setNewUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="newPassword">Nueva Contrase√±a:</label>
                <input
                  type="password"
                  id="newPassword"
                  placeholder="Elige una contrase√±a"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="confirmPassword">Confirmar Contrase√±a:</label>
                <input
                  type="password"
                  id="confirmPassword"
                  placeholder="Confirma tu contrase√±a"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-success">Registrar</button>
              <button type="button" onClick={() => { setShowLogin(true); clearMsg(); }} className="btn btn-link">
                Volver al Login
              </button>
            </form>
          </>
        )}

        {message && (
          <div className={`message ${messageType}`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
