import React, { useState, useEffect } from 'react';
import './styles.css';

const API_BASE = 'http://localhost:3001';

function App() {
  const [showLogin, setShowLogin] = useState(true);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  // Login form state
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Register form state
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const showMsg = (text, type) => {
    setMessage(text);
    setMessageType(type);
  };

  const clearMsg = () => {
    setMessage('');
    setMessageType('');
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    clearMsg();

    try {
      const response = await fetch(`${API_BASE}/user/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
      });

      const data = await response.json();

      if (!response.ok) {
        showMsg(data.message || 'Error en login', 'error');
        return;
      }

      localStorage.setItem('currentUser', JSON.stringify(data.data));
      showMsg('Login exitoso! Redirigiendo...', 'success');
      setTimeout(() => window.location.href = 'mapa.html', 1000);
    } catch (error) {
      showMsg('Error al conectar con el servidor', 'error');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    clearMsg();

    if (newPassword !== confirmPassword) {
      showMsg('Las contraseñas no coinciden', 'error');
      return;
    }

    try {
      // Verificar si el usuario ya existe
      const response = await fetch(`${API_BASE}/user`);
      const users = await response.json();

      const userExists = users.find(u => u.username === newUsername);
      if (userExists) {
        showMsg('El usuario ya existe', 'error');
        return;
      }

      // Crear nuevo usuario
      const newUser = { username: newUsername, password: newPassword };
      const createResponse = await fetch(`${API_BASE}/user`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newUser)
      });

      if (createResponse.ok) {
        showMsg('Usuario creado exitosamente! Ahora puedes iniciar sesión.', 'success');
        setNewUsername('');
        setNewPassword('');
        setConfirmPassword('');
        setTimeout(() => setShowLogin(true), 1500);
      } else {
        showMsg('Error al crear el usuario', 'error');
      }
    } catch (error) {
      showMsg('Error al conectar con el servidor', 'error');
    }
  };

  // Verificar si ya hay un usuario logueado
  React.useEffect(() => {
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      window.location.href = 'mapa.html';
    }
  }, []);

  return (
    <div className="login-container">
      <div className="login-form">
        <h1><i className="fas fa-map-marked-alt"></i> Turismo App</h1>

        {showLogin ? (
          <>
            <form onSubmit={handleLogin}>
              <div className="form-group">
                <label htmlFor="username">Usuario:</label>
                <input
                  type="text"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="password">Contraseña:</label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary">Iniciar Sesión</button>
            </form>

            <div className="separator">o</div>

            <button onClick={() => { setShowLogin(false); clearMsg(); }} className="btn btn-secondary">
              Crear Nueva Cuenta
            </button>
          </>
        ) : (
          <>
            <form onSubmit={handleRegister}>
              <h3>Registrar Nuevo Usuario</h3>
              <div className="form-group">
                <label htmlFor="newUsername">Nuevo Usuario:</label>
                <input
                  type="text"
                  id="newUsername"
                  value={newUsername}
                  onChange={(e) => setNewUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="newPassword">Nueva Contraseña:</label>
                <input
                  type="password"
                  id="newPassword"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="confirmPassword">Confirmar Contraseña:</label>
                <input
                  type="password"
                  id="confirmPassword"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-success">Registrar</button>
              <button type="button" onClick={() => { setShowLogin(true); clearMsg(); }} className="btn btn-link">
                Volver al Login
              </button>
            </form>
          </>
        )}

        <div className={`message ${messageType}`}>
          {message}
        </div>
      </div>
    </div>
  );
}

export default App;
